mapper.select:
SELECT COUNT(id) as cnt
FROM members
WHERE id = #{id}

SELECT memberno, gradeno, id, passwd, mname, nickname, email, tel, zipcode, address1, address2, birth, points, mdate
FROM members
WHERE memberno = #{memberno}

mapper.update:
UPDATE members
SET passwd=#{passwd}, mname=#{mname}, nickname=#{nickname}, email=#{email}, tel=#{tel}, zipcode=#{zipcode}, 
    address1=#{address1}, address2=#{address2}, birth=#{birth}
WHERE memberno = #{memberno}

UPDATE members 
SET points=#{points}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM members
WHERE memberno=#{memberno} AND passwd=#{passwd}

mapper.update:
UPDATE members
SET passwd=#{passwd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM members
WHERE id=#{id} AND passwd=#{passwd}

SELECT memberno, id, passwd, mname, nickname, tel, email, zipcode, address1, address2, mdate
FROM members
WHERE id=#{id}
