mapper.select:
SELECT memberno, mname, nickname, gradeno, mdate 
FROM members
WHERE id=#{id}

SELECT COUNT(id) as cnt
FROM members
WHERE id=#{id}

SELECT COUNT(memberno) as cnt
FROM blacklist
WHERE memberno=#{memberno}

SELECT blacklistno, reason, fromdate, todate, memberno, adminno
FROM blacklist
WHERE blacklistno = #{blacklistno}

SELECT blacklistno, reason, fromdate, todate, memberno, adminno
FROM blacklist
WHERE memberno = #{memberno}

mapper.update:
UPDATE blacklist 
SET todate=todate+#{plusdate}
WHERE blacklistno = #{blacklistno}
