mapper.select:
SELECT p_no, p_name, p_price, thumb1, p_quantity
FROM product
WHERE p_no = #{p_no}

SELECT p_no, goryno, p_name, p_printout, p_date, p_price, p_unit, p_capacity, p_delivery, p_allergy_info, p_expiration_date, p_detailed, p_quantity, searchword,file1, thumb1, size1, r FROM ( SELECT p_no, goryno, p_name, p_printout, p_date, p_price, p_unit, p_capacity, p_delivery, p_allergy_info, p_expiration_date, p_detailed, p_quantity, searchword,file1, thumb1, size1, rownum as r FROM ( SELECT p_no, goryno, p_name, p_printout, p_date, p_price, p_unit, p_capacity, p_delivery, p_allergy_info, p_expiration_date, p_detailed, p_quantity, searchword,file1, thumb1, size1 FROM product
WHERE goryno=#{goryno}
WHERE goryno=#{goryno} AND (p_name LIKE '%' || #{searchword} || '%' OR p_detailed LIKE '%' || #{searchword} || '%' OR searchword LIKE '%' || #{searchword} || '%')
ORDER BY p_no DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

SELECT COUNT(*) as cnt FROM product
WHERE goryno=#{goryno}
WHERE goryno=#{goryno} AND (p_name LIKE '%' || #{searchword} || '%' OR p_detailed LIKE '%' || #{searchword} || '%' OR searchword LIKE '%' || #{searchword} || '%')

mapper.update:
UPDATE product
SET p_name=#{p_name}, p_printout=#{p_printout}, p_price=#{p_price}, p_unit=#{p_unit}, p_capacity=#{p_capacity}, p_delivery=#{p_delivery}, p_allergy_info=#{p_allergy_info},
    p_expiration_date=#{p_expiration_date}, p_detailed=#{p_detailed}, p_quantity=#{p_quantity}, searchword=#{searchword}
WHERE p_no = #{p_no}

UPDATE product
  SET p_quantity=#{p_quantity}
  WHERE p_no = #{p_no}
