mapper.select:
SELECT m_memberno, m_nickname, recipeno, memberno, recipecateno, title, recom, hits, replycnt, file1, thumb1, size1, rating, rating_cnt, rating_sum, r FROM ( SELECT m_memberno, m_nickname, recipeno, memberno, recipecateno, title, recom, hits, replycnt, file1, thumb1, size1, rating, rating_cnt, rating_sum, rownum as r FROM ( SELECT m.memberno as m_memberno, m.nickname as m_nickname, c.recipeno, c.memberno, c.recipecateno, c.title, c.recom, c.hits, c.replycnt, c.file1, c.thumb1, c.size1, c.rating, c.rating_cnt, c.rating_sum FROM members m, recipe c
WHERE (m.memberno = c.memberno) AND c.recipecateno=#{recipecateno}
WHERE (m.memberno = c.memberno) AND c.recipecateno=#{recipecateno} AND (c.title LIKE '%' || #{word} || '%' OR c.introdfood LIKE '%' || #{word} || '%' OR c.ingredfood LIKE '%' || #{word} || '%' OR c.tip LIKE '%' || #{word} || '%' OR c.word LIKE '%' || #{word} || '%')
ORDER BY c.recipeno DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

SELECT COUNT(*) as cnt FROM recipe
WHERE recipecateno=#{recipecateno}
WHERE recipecateno=#{recipecateno} AND (title LIKE '%' || #{word} || '%' OR introdfood LIKE '%' || #{word} || '%' OR ingredfood LIKE '%' || #{word} || '%' OR tip LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')

mapper.update:
UPDATE recipe 
SET rating_cnt = rating_cnt + 1 
WHERE recipeno=#{recipeno}

UPDATE recipe 
SET rating_sum = rating_sum + #{rating_sum}
WHERE recipeno=#{recipeno}

UPDATE recipe 
SET rating = #{rating}
WHERE recipeno=#{recipeno}

UPDATE recipe 
SET hits = hits + 1
WHERE recipeno=#{recipeno}

