mapper.select:
SELECT p_no, reviewno, title, nickname, rdate, content, file1
FROM (
        SELECT p_no, reviewno, title, nickname, rdate, content, file1, rownum as r
        FROM (
                SELECT p.p_no,
                       r.reviewno, r.title, m.nickname, r.rdate, r.content, r.file1
                FROM product p,  review r, members m
                WHERE (p.p_no = r.p_no) AND r.p_no=#{p_no}
                ORDER BY r.reviewno DESC
        )
)
WHERE  r >= #{startNum} AND r <= #{endNum}
